import React, {useState} from 'react';
import Modal from '../UI/Modal';
import classes from './Cart.module.css';


const Cart = props =>{
    const [modalStatus, setModalStatus] =useState(false);

    const modalHandler =()=>{
        modalStatus? setModalStatus(false) : setModalStatus(true);
    };

    const cartItems = <ul className={classes['cart-items']}>{[{id:'c1', name: 'Sushi', amount: 2, price: 12.99}].map(item=> <li>{item.name}</li>)}</ul>;
  return (
  <Modal>
    {cartItems}
    <div className={classes.total}>
        <span>Total Amount</span>
        <span>35.62</span>
    </div>
    <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={closemodalHandler}>Close</button>
        <button className={classes.button}>Order</button>
    </div>
  </Modal>
)};

export default Cart;
