    const cartItems = <ul className={classes['cart-items']}>{cartCtx.items.map(item=> <li>{item.name}</li>)}</ul>;
  return (
  <Modal onClose={props.onClose}>
    {cartItems}
    <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
    </div>
